using System;using System.ComponentModel.DataAnnotations;using System.ComponentModel.DataAnnotations.Schema;namespace RuntimeStuff.MSTests.DTO{    [Table("test", Schema = "dbo")]    public class Test    {        [Key]        [Column("id")]        public long Id { get; set; }        [Column("bool")]        public bool? Bool { get; set; }        [Column("date")]        public DateTime? Date { get; set; }        [Column("datetime")]        public DateTime? Datetime { get; set; }        [Column("datetime2")]        public DateTime? Datetime2 { get; set; }        [Column("image")]        public string Image { get; set; }        [Column("money")]        public decimal? Money { get; set; }        [Column("name")]        public string Name { get; set; }        [Column("text")]        public string Text { get; set; }        [Column("xml")]        public string Xml { get; set; }    }}